#!/bin/sh
( cd $HOME && wget https://github.com/shirkdog/pulledpork/archive/v${pulledpork_version}.tar.gz -O pulledpork-${pulledpork_version}.tar.gz && tar zxvpf pulledpork-${pulledpork_version}.tar.gz && (cd pulledpork-${pulledpork_version} && cp pulledpork.pl /usr/local/bin && chmod +x /usr/local/bin/pulledpork.pl && mkdir -p /etc/snort && cp etc/*.conf /etc/snort) && rm -r pulledpork-${pulledpork_version} pulledpork-${pulledpork_version}.tar.gz )
